mapper.select:
SELECT id, shop_id, user_id, amount, visit_time, items, created_at
FROM order_record
WHERE id = #{id}

SELECT id, shop_id, user_id, amount, visit_time, items, created_at
FROM order_record
WHERE user_id = #{userId}
  AND shop_id = #{shopId}
ORDER BY visit_time DESC, created_at DESC
LIMIT 1

SELECT COUNT(*)
FROM order_record
WHERE created_at >= #{startDate}
  AND created_at <= #{endDate}
